import React, { useEffect } from 'react';
import Axios from 'axios';

const Login = () => {

    useEffect(() => {
        // Check if a token exists in localStorage when the component mounts
        const token = localStorage.getItem('token');
        if (token) {
            // Perform any necessary actions with the token, such as redirecting to a protected route
            console.log('Token exists:', token);
        }
    }, []);

    const handleSubmit = (event) => {
        event.preventDefault();
        const formData = new FormData(event.currentTarget);
        const email = formData.get('email');
        const password = formData.get('password');

        console.log('Email:', email);
        console.log('Password:', password);

        // Perform Axios request to obtain a token
        Axios.post('/login', { email, password })
            .then(response => {
                // Assume the server responds with a token
                const token = response.data.token;

                // Save the token to localStorage
                localStorage.setItem('token', token);

                // Perform any additional actions with the obtained token
                console.log('Token obtained:', token);
            })
            .catch(error => {
                // Handle errors
                console.error('Error:', error);
            });
    }

    return (
        <div className="container">
            <div className="row justify-content-center">
                <div className="col-md-6">
                    <div className="card">
                        <div className="card-body">
                            <h5 className="card-title">Login</h5>
                            <form onSubmit={handleSubmit}>
                                <div className="mb-3">
                                    <label htmlFor="email" className="form-label">Email</label>
                                    <input type="email" className="form-control" id="email" name="email" />
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="password" className="form-label">Password</label>
                                    <input type="password" className="form-control" id="password" name="password" />
                                </div>
                                <button type="submit" className="btn btn-primary" onClick={handleSubmit}>Submit</button>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Login;
